import React, { useEffect, useState } from "react";
import axios from "axios";

export default function ProjectList({ skill }) {
  const [projects, setProjects] = useState([]);

  useEffect(() => {
    const url = skill 
      ? `http://localhost:5000/projects/skill/${skill}` 
      : `http://localhost:5000/projects`;
    axios.get(url).then(res => setProjects(res.data));
  }, [skill]);

  return (
    <div>
      <h3>Projects {skill && `(Filtered by ${skill})`}</h3>
      <ul>
        {projects.map(p => <li key={p._id}>{p.title} - {p.skills.join(", ")}</li>)}
      </ul>
    </div>
  );
}
